            title: 'Stars',
            showStatus: false,
            arr: [
              { title: '4', status: false },
              { title: '5', status: false },
              { title: '6', status: false },
              { title: '7', status: false },
              { title: '8', status: false },
            ]
          },
          {
            id: 7,
            title: 'Rarity',
            showStatus: false,
            arr: [
              { title: 'Normal', status: false },
              { title: 'Medium', status: false },
              { title: 'Rare', status: false },
              { title: 'Epic', status: false },
              { title: 'Legend', status: false }
            ]
          },
        )
      }
    },
    MinInputClick (data) {
      console.log('data: ', data);
    },
    MaxInputClick (data) {
      console.log('data: ', data);
    },
    closeSelect (item, index) {
      if (item.selectItem) {
        this.navArr.forEach(ele => {
          if (ele.id == item.id) {
            ele.arr[item.index].status = false
          }
        })
      }
      if (item.id == 3 || item.index == 1) {
        this.navArr.forEach((a, i) => {
          if (a.id == 6) {
            this.navArr.splice(i, 2)
          }
        })
      }
      if (item.id == 3 || item.index == 0) {
        this.navArr.forEach((a, i) => {
          if (a.id == 2) {
            this.navArr.splice(i, 1)
          }
        })
      }
      this.selectArr.splice(index, 1)
    },
    clearBtn () {
      this.selectArr = []
      this.navArr.forEach((item, i) => {
        item.arr.forEach(ele => {
          ele.status = false
        })
        if (item.id == 2) {
          this.navArr.splice(i, 1)
        }
        if (item.id == 6) {
          this.navArr.splice(i, 2)
        }
      })
    },
    SearchInputClick (data) {
      console.log('data: ', data);
    },
    sortClik (data) {
      this.disablehover = true;
      setTimeout(() => {
        this.disablehover = false;
      }, 600);
      this.sortTXT = data.name
      // console.log('data: ', data);
    },
    nftFun (item) {
      if (this.showSelect) {
        item.selectStatus = !item.selectStatus
      } else {
        if (item.nft) {
          this.videoStatus = true
          this.videoSrc = item.videoSrc
        }
      }
      console.log('装备信息item: ', item);
    },
    closeProup () {
      this.videoStatus = false
    },
    sellPageClick () {
      this.sellPageStatus = false
    },
    // 返回市场页面
    backClick () {
      this.showSelect = false
      this.sellPageStatus = this.historyStatus = true
      this.nftArr.forEach(item => {
        item.showSelect = false
        item.selectStatus = false
      })
    },
    // 是否展示选择框方法
    showSelectClick () {
      this.showSelect = !this.showSelect
      this.nftArr.forEach(item => {
        item.showSelect = this.showSelect
        item.selectStatus = false
      })
    },
    closeOrder (data) {
      if(data){
        // this.nftArr.forEach(item => {
        //   item.showSelect = true
        //   item.selectStatus = false
        // })
        this.integrationBoxAnsNft()
      }
      this.orderStatus = false
    },
    // 整合盲盒数据跟nft数据
    integrationBoxAnsNft(){
      console.log("执行此函数")
      let userBoxArr = JSON.parse(sessionStorage.getItem("setBoxInfo"))
      // console.log('userBoxArr: ', userBoxArr);
      let userNftArr = JSON.parse(localStorage.getItem("nftInfo"))
      // console.log('userNftArr: ', userNftArr);
      if(userNftArr){
        // console.log("用户装备数组真的")
        this.nftArr = userNftArr.concat(userBoxArr)
        this.loadMoreStatus = false
      }else{
        // console.log("用户装备数组假的")
        this.$utils.getUserBindbox(this.getAccount,0,10000000).then(res => {
          console.log('使用公共方法获取到的数据: ',res);
          if(res.length > 0){
            this.nftArr = res.concat(userBoxArr)
            localStorage.setItem('nftInfo',JSON.stringify(res))
            this.loadMoreStatus = false
          }else{
            this.nftArr = userBoxArr
            this.loadMoreStatus = false
          }
        })
      }
      console.log('this.nftArr: ', this.nftArr);
    }
  }
}
